import { createSlice, PayloadAction } from '@reduxjs/toolkit';

// Definiowanie typu dla samochodu
interface Car {
  id: number;
  type: 'sedan' | 'SUV' | 'van';
  available: boolean;
}

// Definiowanie typu dla stanu
interface CarState {
  availableCars: Car[];
  rentals: { carId: number; startDate: string; days: number }[];
}

// Przykładowa lista samochodów na początku
const initialState: CarState = {
  availableCars: [
    { id: 1, type: 'sedan', available: true },
    { id: 2, type: 'SUV', available: true },
    { id: 3, type: 'van', available: true },
  ],
  rentals: [],
};

const carSlice = createSlice({
  name: 'cars',
  initialState,
  reducers: {
    reserveCar: (state, action: PayloadAction<{ carId: number; startDate: string; days: number }>) => {
      const carIndex = state.availableCars.findIndex(car => car.id === action.payload.carId);
      if (carIndex >= 0) {
        state.availableCars[carIndex].available = false;
        state.rentals.push(action.payload);
      }
    },
    returnCar: (state, action: PayloadAction<number>) => {
      const rentalIndex = state.rentals.findIndex(rental => rental.carId === action.payload);
      if (rentalIndex >= 0) {
        const carIndex = state.availableCars.findIndex(car => car.id === action.payload);
        if (carIndex >= 0) {
          state.availableCars[carIndex].available = true;
        }
        state.rentals.splice(rentalIndex, 1);
      }
    },
  },
});

export const { reserveCar, returnCar } = carSlice.actions;
export default carSlice.reducer;
