import { createSlice, PayloadAction } from '@reduxjs/toolkit';

// Definicja interfejsu dla pojedynczego samochodu
interface Car {
  id: number;
  type: 'sedan' | 'SUV' | 'van';
  available: boolean;
}

// Definicja interfejsu dla stanu aplikacji
interface CarState {
  availableCars: Car[];  // Lista dostępnych samochodów
  rentals: { carId: number; startDate: string; days: number }[]; // Lista wypożyczeń
}

// Inicjalny stan aplikacji
const initialState: CarState = {
  availableCars: [
    { id: 1, type: 'sedan', available: true },
    { id: 2, type: 'SUV', available: true },
    { id: 3, type: 'van', available: true },
  ],
  rentals: [],
};

const carSlice = createSlice({
  name: 'cars',
  initialState,
  reducers: {
    reserveCar: (state, action: PayloadAction<{ carId: number; startDate: string; days: number }>) => {
      const carIndex = state.availableCars.findIndex(car => car.id === action.payload.carId);
      if (carIndex >= 0) {
        state.availableCars[carIndex].available = false; // Rezerwacja samochodu
        state.rentals.push(action.payload); // Dodanie do historii wypożyczeń
      }
    },
    returnCar: (state, action: PayloadAction<number>) => {
      const carIndex = state.availableCars.findIndex(car => car.id === action.payload);
      if (carIndex >= 0) {
        state.availableCars[carIndex].available = true; // Zwrócenie samochodu
        state.rentals = state.rentals.filter(rental => rental.carId !== action.payload); // Usunięcie wypożyczenia
      }
    },
  },
});

export const { reserveCar, returnCar } = carSlice.actions;
export default carSlice.reducer;
