import React from 'react';
import { useSelector } from 'react-redux'; // Import useSelector z Reduxa
import { Provider } from 'react-redux';
import { store, RootState } from './store/store';
import CarList from './components/CarList';
import ReservationForm from './components/ReservationForm';
import RentalHistory from './components/RentalHistory';
import './styles/App.scss';

const App: React.FC = () => {
  const rentals = useSelector((state: RootState) => state.cars.rentals); // Pobieramy wypożyczenia z Reduxa

  return (
    <Provider store={store}> {/* Zapewniamy, że Provider otacza całą aplikację */}
      <div className="app-container">
        <ReservationForm />
        {/* Logika warunkowa: wyświetlamy kolumnę tylko, gdy są wypożyczenia */}
        <div className={`columns-container ${rentals.length > 0 ? '' : 'columns-container--single-column'}`}>
          <div className="columns-container__column">
            <CarList />
          </div>
          {rentals.length > 0 && (
            <div className="columns-container__column">
              <RentalHistory />
            </div>
          )}
        </div>
      </div>
    </Provider>
  );
};

export default App;
