import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { reserveCar } from '../features/cars/carSlice';
import { RootState } from '../store/store';

const ReservationForm: React.FC = () => {
  const dispatch = useDispatch();
  const availableCars = useSelector((state: RootState) => state.cars.availableCars);

  const [carId, setCarId] = useState<number>(1);
  const [startDate, setStartDate] = useState<string>('');
  const [days, setDays] = useState<number>(1);

  const selectedCar = availableCars.find(car => car.id === carId);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (selectedCar) {
      // Przekazujemy id, typ samochodu oraz datę wypożyczenia
      dispatch(reserveCar({ carId, type: selectedCar.type, startDate, days }));
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Reserve a Car</h2>
      <div>
        <label>Car Type</label>
        <select value={carId} onChange={(e) => setCarId(Number(e.target.value))}>
          {availableCars.map((car) => (
            <option key={car.id} value={car.id}>{car.type}</option>
          ))}
        </select>
      </div>
      <div>
        <label>Start Date</label>
        <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
      </div>
      <div>
        <label>Days</label>
        <input type="number" value={days} onChange={(e) => setDays(Number(e.target.value))} />
      </div>
      <button type="submit">Reserve</button>
    </form>
  );
};

export default ReservationForm;
